function arrayToXml($array, $rootElement = 'Colors', $nodeBlock = 'Color') {\n    $xml = new SimpleXMLElement("<?xml version='1.0' encoding='UTF-8'?><$rootElement></$rootElement>");\n    foreach ($array as $item) {\n        $child = $xml->addChild($nodeBlock);\n        foreach ($item as $key => $value) {\n            $child->addChild($key, htmlspecialchars($value));\n        }\n    }\n    return $xml->asXML();\n}\n\n// Example usage\n$colors = array(\n    0 => ['id' => 1, 'color' => 'blue'],\n    1 => ['id' => 2, 'color' => 'green'],\n);\n\n$xmlOutput = arrayToXml($colors);\necho $xmlOutput;