<?php\n   header('Content-Type: text/plain; charset=utf-8');\n\n   // Assuming $link is your mysqli connection object\n\n   if ($_POST['action'] == 'i') {\n       // Prepare and bind\n       $stmt = $link->prepare("INSERT INTO app_notes (username, note, created_at) VALUES (?, ?, NOW())");\n       $stmt->bind_param("ss", $_POST['username'], $_POST['note']);\n\n       // Execute the statement\n       if ($stmt->execute()) {\n           echo '1 - ' . $stmt->insert_id;\n       } else {\n           echo '0 - Ins';\n       }\n\n       // Close the statement\n       $stmt->close();\n   }\n   ?>