function isPdf($filePath): bool {\n    // Open the file in binary read mode\n    $f=fopen($filePath, "rb");\n    \n    // Read the first few bytes of the file\n    $bytes = fread($f, 4);\n    \n    // The magic numbers for PDF files are '%PDF'\n    if (strpos($bytes, '%PDF') === 0) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n// Usage example:\n$uploadedFile = $_FILES['userfile']['tmp_name'];\nif(isPdf($uploadedFile)) {\n    echo "The uploaded file is a PDF.";\n} else {\n    echo "The uploaded file is not a PDF.";\n}