$searchTerm = $_GET['query'];\n\n// Sanitize input to prevent SQL Injection\n$searchTerm = mysqli_real_escape_string($connection, $searchTerm);\n\n$query = "SELECT post_id, post_subject \n          FROM phpbb_posts \n          WHERE MATCH(post_text) AGAINST('+" . $searchTerm . "*' IN BOOLEAN MODE)";\n\n$result = mysqli_query($connection, $query);\nwhile ($row = mysqli_fetch_assoc($result)) {\n    echo "Post ID: " . htmlspecialchars($row['post_id']) . ", Subject: " . htmlspecialchars($row['post_subject']) . "<br>";\n}