<?php\n$inputFile = 'input.gif';\n$outputFile = 'output_resized.gif';\n\n// Desired dimensions\n$width = 200;\n$height = 200;\n\n// Generate the command to resize the GIF\n$command = "convert {$inputFile} -resize '{$width}x{$height}' {$outputFile}";\n\n// Execute the command\nexec($command, $output, $return_var);\n\nif ($return_var === 0) {\n    echo "GIF resized successfully.";\n} else {\n    echo "Error resizing GIF: ";\n    print_r($output);\n}\n?>