function exportNamesCommentsCSV($table, $filename = 'volunteer_2009_comments.csv') {\n    $csv_terminated = "\n";\n    $csv_separator = ",";\n    $csv_enclosed = '"';\n    $csv_escaped = "\\";\n\n    // Define custom headers here\n    $custom_headers = ['First Name', 'Last Name', 'Email', 'Comments'];\n\n    // Your SQL query remains the same, but ensure it matches the order of $custom_headers\n    $sql_query = "SELECT lname, fname, email, comments FROM volunteers_2009";\n\n    // Get data from database\n    $result = mysql_query($sql_query);\n    $fields_cnt = mysql_num_fields($result);\n\n    $schema_insert = '';\n\n    // Use custom headers\n    foreach ($custom_headers as $header) {\n        $l = $csv_enclosed . str_replace($csv_enclosed, $csv_escaped . $csv_enclosed, stripslashes($header)) . $csv_enclosed;\n        $schema_insert .= $l;\n        $schema_insert .= $csv_separator;\n    }\n\n    // Remove the last separator and add terminator\n    $out = trim(substr($schema_insert, 0, -1));\n    $out .= $csv_terminated;\n\n    // Format data\n    while ($row = mysql_fetch_array($result)) {\n        $schema_insert = '';\n        for ($j = 0; $j < $fields_cnt; $j++) {\n            if ($row[$j] == '0' || $row[$j] != '') {\n\n                if ($csv_enclosed == '') {\n                    $schema_insert .= $row[$j];\n                } else {\n                    $schema_insert .= $csv_enclosed . \n                    str_replace($csv_enclosed, $csv_escaped . $csv_enclosed, $row[$j]) . $csv_enclosed;\n                }\n            } else {\n                $schema_insert .= '';\n            }\n\n            if ($j < $fields_cnt - 1) {\n                $schema_insert .= $csv_separator;\n            }\n        } // end for\n\n        $out .= $schema_insert;\n        $out .= $csv_terminated;\n    } // end while\n\n    header("Cache-Control: must-revalidate, post-check=0, pre-check=0");\n    header("Content-Length: " . strlen($out));\n    header("Content-type: text/x-csv");\n    header("Content-Disposition: attachment; filename=$filename");\n    echo $out;\n    exit;\n}