<?php\n// Assume $mysqli is your database connection object\n\n// Check if the user is logged in (this should be defined by your authentication mechanism)\n$isLoggedIn = isset($_SESSION['user_id']); // Example of checking session for login status\n\n// Prepare and execute SQL query to fetch the status\n$stmt = $mysqli->prepare("SELECT status FROM your_table_name WHERE some_condition = ?");\n$stmt->bind_param("s", $someValue); // Bind parameters depending on your query condition\n$stmt->execute();\n$result = $stmt->get_result();\n\n$row = $result->fetch_assoc();\n$status = $row['status'] ?? '';\n\n// Check both conditions and decide whether to show the element or not\nif ($status === 'closed' && $isLoggedIn) {\n    // Do not display the HTML element\n} else {\n    // Display the HTML element\n    echo '<div>Your HTML content here</div>';\n}\n\n$stmt->close();\n?>