<?php\n\n$loginUrl = 'http://highlearn.website/login_page.asp?userID=foo&password=bar';\n$dataPageUrl = 'http://highlearn.website/data_page.asp';\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set options for the login request\ncurl_setopt($ch, CURLOPT_URL, $loginUrl);\ncurl_setopt($ch, CURLOPT_COOKIEJAR, "cookie.txt"); // Store cookies in a file called cookie.txt\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the login request to set the cookies\n$response = curl_exec($ch);\n\n// Check for cURL errors and output any error messages\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n}\n\n// Set options for the data page request\ncurl_setopt($ch, CURLOPT_URL, $dataPageUrl);\ncurl_setopt($ch, CURLOPT_COOKIEFILE, "cookie.txt"); // Use cookies from cookie.txt\n\n// Execute the data page request using the logged-in session\n$dataResponse = curl_exec($ch);\n\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    // Process the dataResponse as needed, such as parsing it for RSS feed content\n    echo htmlspecialchars($dataResponse);\n}\n\n// Close cURL session\ncurl_close($ch);\n\n?>