$lines = file('english.php');\n$keys = array(); // Initialize $keys as an empty array\n$values = array(); // Initialize $values as an empty array\n\nforeach($lines as $line) {\n    $matches = array();\n    if (preg_match('/DEFINE\(\'(.*?)\',\s*\'(.*)\'\);/i', trim($line), $matches)) { // Use trim() to remove any line endings\n        $keys[] = $matches[1];\n        $values[] = $matches[2];\n    }\n}\n\nif (!empty($keys) && !empty($values)) {\n    $lang = array_combine($keys, $values);\n} else {\n    $lang = array(); // Fallback to an empty array if no keys or values are found\n}