if(isset($_GET["m"])) {\n    $module_name = $_GET["m"];\n    if(isset($module_exists[$module_name]) && method_exists($module_name, "view")) { // Yep\n        // Load view (should be an array)\n        $module_view = forward_static_call([$module_name, 'view']);\n        if(!is_array($module_view)) { // Not an array :(\n            error::e500module($module_name, $_SERVER["REQUEST_URI"]);\n        }\n    } else { // Nope, so display error\n        error::e404($_SERVER['REQUEST_URI']);\n    }\n}