require_once 'path/to/Imap.php';\n\n// Set up the IMAP connection parameters for POP3\n$mailbox = '{pop.example.com:110/pop3/novalidate-cert}INBOX';\n$username = 'your_email@example.com';\n$password = 'your_password';\n\n$imap = new \PhpImap\Mailbox($mailbox, $username, $password);\n\ntry {\n    // Get emails from the inbox\n    $emailsIds = $imap->searchMailbox('ALL');\n    \n    if (count($emailsIds)) {\n        foreach ($emailsIds as $emailId) {\n            $mail = $imap->getMail($emailId);\n            \n            // Output email details\n            echo 'Subject: ', htmlspecialchars($mail->subject), PHP_EOL;\n            echo 'From: ', htmlspecialchars($mail->fromAddress), PHP_EOL;\n            echo 'Date: ', $mail->date, PHP_EOL;\n            echo 'Message body part (HTML or plain): ', nl2br(htmlspecialchars($mail->text)), PHP_EOL;\n            \n            // Optionally delete the email\n            // $imap->deleteMail($emailId);\n        }\n        \n        // Expunge deleted emails from server\n        $imap->expunge();\n    } else {\n        echo 'No emails found.';\n    }\n} catch (\Exception $e) {\n    die('Failed to read mailbox: ' . $e->getMessage());\n}