// Set default timezone\ndate_default_timezone_set('UTC');\n\n// Query to insert data (assuming $mysqli is your connection object)\n$time = date('Y-m-d H:i:s');\n$mysqli->query("INSERT INTO my_table (`event_time`) VALUES ('$time')");\n\n// Fetching data and converting to user's timezone\n$userTimeZone = 'America/New_York';\n$result = $mysqli->query("SELECT `event_time` FROM my_table");\n$row = $result->fetch_assoc();\n$utcTime = new DateTime($row['event_time'], new DateTimeZone('UTC'));\n$utcTime->setTimezone(new DateTimeZone($userTimeZone));\necho $utcTime->format('Y-m-d H:i:s');