// -- parser.php --\n$pet = "lamb";\n\n$fileContents = file_get_contents('myFile.txt');\n\npreg_match_all('/\$(\w+)/', $fileContents, $matches);\n$vars = get_defined_vars();\n\nforeach ($matches[1] as $varName) {\n    if (array_key_exists($varName, $vars)) {\n        $fileContents = str_replace('$' . $varName, $vars[$varName], $fileContents);\n    }\n}\n\n$newContents = $fileContents;\necho $newContents; // Outputs: Mary had a little lamb.