amespace CRM\Plugins;\n\nclass ExamplePlugin {\n    private $config;\n\n    public function __construct($config) {\n        $this->config = $config;\n\n        if ($this->config['enabled']) {\n            add_filter('user_list_item', [$this, 'modifyUserListItem']);\n        }\n    }\n\n    public function modifyUserListItem($itemHtml) {\n        // Example modification: make usernames clickable\n        return preg_replace('/<a href="\/users\/(\d+)">([^<]+)<\/a>/',\n            '<a href="#" onclick="openLightBox(\'/user_info/$1\')">$2</a>',\n            $itemHtml);\n    }\n}