<?php\n$domain = 'www.example.com';\n$domainWithDot = '.www.example.com'; // Include leading dot for subdomains\n$deleteExpiration = time() - 60*60*24*365*10; // 10 years ago\n\nforeach (array_keys($_COOKIE) as $cookie) {\n    setcookie($cookie, '', $deleteExpiration, '/', $domain);        // Try without leading dot\n    setcookie($cookie, '', $deleteExpiration, '/', $domainWithDot); // Try with leading dot\n}\n?>