try {\n    $pdo = new PDO('sqlite:path/to/your/database.db', null, null,\n        array(PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION)\n    );\n    // Set the encoding to UTF-8 by executing this query\n    $pdo->exec("PRAGMA encoding=UTF8;");\n} catch (\PDOException $e) {\n    echo "An error occurred: " . $e->getMessage();\n}\n\n// Example of using the PDO connection\n$stmt = $pdo->prepare('SELECT * FROM your_table WHERE column_name = ?');\n$stmt->execute(['some_value']);\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);