<?php\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL where you want to send the request\ncurl_setopt($ch, CURLOPT_URL, "https://service.com/signin.php");\n\n// Set that we are sending POST data\ncurl_setopt($ch, CURLOPT_POST, 1);\n\n// Data payload for login (username and password)\n$postData = [\n    'username' => 'your_username',\n    'password' => 'your_password'\n];\n\n// Convert array to URL-encoded query string\n$postString = http_build_query($postData);\n\n// Attach our encoded POST fields to the cURL session\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $postString);\n\n// Tell curl not to return headers but just the response\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute and fetch the response\n$response = curl_exec($ch);\n\n// Check for errors and handle them\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    // Print out the response from the server\n    echo $response;\n}\n\n// Close cURL session\ncurl_close($ch);\n\n?>