public function parseText($newData) {\n    $textToReplace = "Your text here with {{var}} placeholders";\n\n    // Define an anonymous function (closure) that uses the $newData variable\n    $callback_function = function ($matches) use ($newData) {\n        // Access $newData inside this function\n        return str_replace($matches[0], $newData, $matches[0]);\n    };\n\n    // Use preg_replace_callback with the closure\n    $result = preg_replace_callback('/{{(.*?)}}/', $callback_function, $textToReplace);\n\n    return $result;\n}