<?php\n\nclass A {\n    function fn() {\n        print 'Context: Class:' . get_class($this) \n              . ' Parent:' . get_parent_class($this) . "\n";\n        $parentClass = get_parent_class($this);\n        if ($parentClass && method_exists($parentClass, 'fn')) {\n            parent::fn();\n        }\n    }\n}\n\nclass B extends A { }\nclass C extends B { }\n\n$a = new A();\n$c = new C();\n\n$a->fn();\n\nprint "\n";\n\n$c->fn();\n\n?>