<?php\nheader('Content-Type: text/plain; charset=utf-8');  // optional\n\nerror_reporting(E_ALL | E_STRICT);\n\n$options = array(\n    'http' => array(\n        'method' => 'POST',\n        'header' => implode("\r\n", array(\n            'Content-type: application/x-www-form-urlencoded',\n            'Accept-Language: en-us,en;q=0.5', // optional\n            'Accept-Charset: UTF-8,*;q=0.7' // changed to UTF-8\n        )),\n        'content' => http_build_query(array(\n            'prev'  =>  '_t',\n            'hl'    =>  'en',\n            'ie'    =>  'UTF-8',\n            'text'  =>  'hello',\n            'sl'    =>  'en',\n            'tl'    =>  'ru'\n        ))\n    )\n);\n\n$context = stream_context_create($options);\n$page = file_get_contents('http://translate.google.com/translate_a/single', false, $context); // updated endpoint\n\nif ($page === FALSE) {\n    die('Error');\n}\n\n// Decode JSON response\n$responseData = json_decode($page, true);\n$translation = $responseData[0][0][0];\n\necho $translation;\n?>