<?php\n\n// Sample text containing paragraphs\n$text = "<p>First paragraph</p>\n<p>Second paragraph</p>\n<p>Third paragraph</p>\n<p>Fourth paragraph</p>";\n\n// Regular expression to match content between <p> and </p> tags\npreg_match_all('/<p>(.+?)<\/p>/is', $text, $matches);\n\n// Check if any matches were found\nif (!empty($matches[1])) {\n    // Randomly select one of the matched paragraphs\n    $randomParagraph = $matches[1][array_rand($matches[1])];\n    \n    // Output the randomly selected paragraph\n    echo htmlspecialchars($randomParagraph); // Use htmlspecialchars to prevent XSS\n} else {\n    echo "No paragraphs found.";\n}\n\n?>