class Page {\n    public function __construct() {\n        // Set the error handler using an array with $this and the method name\n        set_error_handler([$this, 'appendError']);\n    }\n\n    public function appendError($errno, $errstr, $errfile = '', $errline = 0) {\n        // Your custom error handling code here\n        // For example, you might log the error or display it in a user-friendly way\n        echo "An error occurred: [$errno] $errstr in $errfile on line $errline";\n    }\n}\n\n// Example usage:\n$page = new Page();\ntrigger_error("Test error", E_USER_NOTICE);  // This should call appendError