class Database {\n    private $pdo;\n\n    public function __construct($host, $dbname, $user, $password) {\n        $this->pdo = new \PDO("mysql:host=$host;dbname=$dbname", $user, $password);\n    }\n\n    public function createSelectQueryBuilder(): SelectQueryBuilder {\n        return new SelectQueryBuilder($this->pdo);\n    }\n}\n\nclass SelectQueryBuilder {\n    private $pdo;\n    private $query;\n\n    public function __construct(\PDO $pdo) {\n        $this->pdo = $pdo;\n        $this->query = '';\n    }\n\n    public function select(...$fields): self {\n        $this->query .= 'SELECT ' . implode(', ', $fields);\n        return $this;\n    }\n\n    public function from(string $table): self {\n        $this->query .= ' FROM ' . $table;\n        return $this;\n    }\n\n    public function where(string $field, string $operator, string $value): self {\n        $this->query .= " WHERE $field $operator ?";\n        return $this;\n    }\n\n    public function get(): array {\n        $stmt = $this->pdo->prepare($this->query);\n        $stmt->execute();\n        return $stmt->fetchAll(\PDO::FETCH_ASSOC);\n    }\n}\n\n$db = new Database('localhost', 'testdb', 'user', 'password');\n$postContent = $db->createSelectQueryBuilder()\n                ->select('id', 'title', 'firstName', 'created', 'catName', 'tagName')\n                ->from('content')\n                ->where('id', '=', '1')\n                ->get();