function convertToPath($string) {\n    // Convert the string to lowercase and trim it\n    $string = strtolower(trim($string));\n    \n    // Replace spaces with dashes\n    $string = str_replace(' ', '-', $string);\n    \n    // Use a regex pattern to match all characters that are not letters, numbers, dashes, or underscores\n    // \p{L} matches any kind of letter from any language\n    // \p{N} matches any kind of numeric character in any script\n    $string = preg_replace('/[^p{L}\p{N}-_]/u', '-', $string);\n    \n    // Replace multiple dashes with a single dash\n    $string = preg_replace('/-+/', "-", $string);\n\n    return $string;\n}