// Load your HTML content into DOMDocument\n$html = '<html><body><div id="removeMe">This will be removed</div></body></html>';\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress warnings from malformed HTML\n$dom->loadHTML($html);\nlibxml_clear_errors(); // Clear any errors after loading\n\n// Find the element to remove by its ID\n$elementToRemove = $dom->getElementById('removeMe');\nif ($elementToRemove) {\n    // Remove the element from its parent node\n    $elementToRemove->parentNode->removeChild($elementToRemove);\n}\n\n// Save or output the modified HTML\necho $dom->saveHTML();