$url = "http://search.twitter.com/search.json?q=&ands=&phrase=&ors=&nots=RT%2C+%40&tag=andyasks&lang=all&from=amcafee&to=&ref=&near=&within=15&units=mi&since=&until=&rpp=50";\n$contents = file_get_contents($url);\n$decode = json_decode($contents, true);\n\n// Set up a PDO connection (make sure to fill in your database credentials)\ntry {\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database_name', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    echo "Connection failed: " . $e->getMessage();\n}\n\n// Prepare an insert statement\n$stmt = $pdo->prepare("INSERT IGNORE INTO andyasks (questions, date, user) VALUES (:questions, :date, 'Andy')");\n\nforeach ($decode['results'] as $current) {\n    if (preg_match("/\?/", "$current[text]")) {\n        // Bind parameters and execute\n        $stmt->execute([':questions' => $current['text'], ':date' => $current['created_at']]);\n    }\n}