function collateVariables() {\n    $obj = new stdClass();\n    \n    // Merge POST and GET into one array, POST values will overwrite any GET conflicts\n    $combinedData = array_merge($_GET, $_POST);\n    \n    // Convert the array to an object\n    foreach ($combinedData as $key => $value) {\n        $obj->$key = $value;\n    }\n    \n    return $obj;\n}\n\n// Usage\n$variablesObject = collateVariables();