<?php\n\n   require('vendor/autoload.php');\n\n   use \Net_DNS2\Resolver;\n\n   try {\n       $resolver = new Resolver();\n\n       // Perform the DNS query for SRV records\n       $result = $resolver->query('_xmpp-server._tcp.gmail.com', 'SRV');\n\n       foreach ($result->answer as $record) {\n           echo "Target: {$record->target}\n";\n           echo "Weight: {$record->weight}\n";\n           echo "Priority: {$record->priority}\n";\n           echo "Port: {$record->port}\n\n";\n       }\n\n   } catch (Exception $e) {\n       die('Failed to query DNS: ' . $e->getMessage());\n   }