<?php\n\nclass Database {\n    private $pdo;\n    private $recordsPerPage = 0;\n    private $pageNumber = 1;\n\n    public function __construct($dsn, $username, $password) {\n        $this->pdo = new PDO($dsn, $username, $password, [\n            PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION\n        ]);\n    }\n\n    public function query($queryString, $params = []) {\n        if (empty($queryString)) {\n            return null;\n        }\n\n        if ($this->recordsPerPage) {\n            $limitOffset = "LIMIT :offset, :length";\n            array_push($params, ['offset' => ($this->pageNumber - 1) * $this->recordsPerPage, PDO::PARAM_INT],\n                         ['length' => $this->recordsPerPage, PDO::PARAM_INT]);\n        } else {\n            $limitOffset = "";\n        }\n\n        $stmt = $this->pdo->prepare($queryString . ' ' . $limitOffset);\n        $stmt->execute($params);\n\n        return $stmt;\n    }\n\n    public function setPagination($recordsPerPage, $pageNumber) {\n        $this->recordsPerPage = $recordsPerPage;\n        $this->pageNumber = $pageNumber;\n    }\n}