function stringToIntHash($string) {\n    // Use a secure hash function like sha256\n    $hash = hash('sha256', $string);\n\n    // Convert the first 8 characters (32 bits) to hexadecimal and then to an integer\n    // Ensure that the result is within the positive integer range for your environment\n    $intHash = hexdec(substr($hash, 0, 8));\n\n    return $intHash;\n}\n\n$string = "exampleString";\n$intHash = stringToIntHash($string);\necho "Integer hash: " . $intHash;