function get_comment_id_from_nid($nid) {\n  // Query the database for comments on a specific node.\n  $query = db_select('comment', 'c')\n    ->fields('c', array('cid'))\n    ->condition('c.nid', $nid)\n    ->range(0, 1); // To get only one comment ID.\n\n  $result = $query->execute();\n  \n  if ($row = $result->fetchAssoc()) {\n    return $row['cid'];\n  }\n\n  return NULL; // Return NULL if no comments are found.\n}