<?php\nif (!empty($_GET['url']) && !empty($_GET['domain'])) {\n    $url = urldecode($_GET['url']);\n    $domain = urldecode($_GET['domain']);\n\n    // Log the entry into your MySQL database or another logging system\n    $logData = [$domain, $_SERVER['REMOTE_ADDR'], date('Y-m-d H:i:s'), $url];\n\n    // Example of how you might log this to a file or DB\n    $fileLogger = fopen("/path/to/access.log", "a");\n    fputcsv($fileLogger, $logData);\n    fclose($fileLogger);\n\n    // Optionally, redirect to the original URL without further processing\n    header("Location: http://$domain$url", true, 302);\n    exit();\n}\n?>