<?php\n$db = new PDO('sqlite:/path/to/your/database.db');\n\n// Ensure the table exists (run this once)\n$db->exec("CREATE TABLE IF NOT EXISTS api_counter (id INTEGER PRIMARY KEY, count INTEGER DEFAULT 0)");\n\n// Function to increment the counter\nfunction incrementCounter($db) {\n    $stmt = $db->prepare("UPDATE api_counter SET count = count + 1 WHERE id = 1");\n    // If no row exists for id=1, insert it with count 1\n    if (!$stmt->execute() || $db->changes() === 0) {\n        $db->exec("INSERT INTO api_counter (count) VALUES (1)");\n    }\n}\n\n// Function to get the current counter value\nfunction getCounter($db) {\n    $stmt = $db->query("SELECT count FROM api_counter WHERE id=1");\n    return (int)$stmt->fetchColumn();\n}\n\nincrementCounter($db);\n$currentCount = getCounter($db);\n\necho "API has been called $currentCount times.";\n?>