class AddToCartForm extends sfForm {\n    public function configure() {\n        $this->setWidgets(array(\n            'productId' => new sfWidgetFormInputHidden(),\n            'quantity'  => new sfWidgetFormInputText(array(), array('value' => '1')),\n        ));\n\n        $this->setValidators(array(\n            'productId' => new sfValidatorInteger(),\n            'quantity'  => new sfValidatorNumber(array('required' => false, 'min' => 1), array('invalid' => 'Please enter a valid quantity.')),\n        ));\n    }\n}