<?php\n// URL where the form is processed\n$url = "http://example.com/process_form.php";\n\n// Data to be sent via POST\n$data = array(\n    'field1' => 'value1',\n    'field2' => 'value2'\n);\n\n// Initialize cURL session\n$ch = curl_init($url);\n\n// Set the option to return the transfer as a string instead of outputting it directly\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Set the option for POST fields\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $data);\n\n// Execute cURL session and fetch response\n$response = curl_exec($ch);\n\n// Check for errors\nif(curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n}\n\n// Close cURL session\ncurl_close($ch);\n\n// Output the server's response (if needed)\necho $response;\n?>