class Score {\n    var $score;\n    var $name;\n    var $dept;\n    var $date;\n\n    function __construct($score, $name, $dept, $date) {\n        $this->score = $score; // Fixed typo here from scores to score\n        $this->name = $name;\n        $this->dept = $dept;\n        $this->date = $date;\n    }\n\n    function getScore(){\n        return $this->score;\n    }\n\n    function getName(){\n        return $this->name;\n    }\n\n    function getDept(){\n        return $this->dept;\n    }\n\n    function getDate(){\n        return $this->date;\n    }\n}\n\n$newscore = new Score("131313", "James", "Marketing", "19/05/2008");\necho "Score: " . $newscore->getScore() . "\n";\necho "Name: " . $newscore->getName() . "\n";\necho "Department: " . $newscore->getDept() . "\n";\necho "Date: " . $newscore->getDate() . "\n";