class Score {\n    var $score;\n    var $name;\n    var $dept;\n    var $date;\n\n    function __construct($score, $name, $dept, $date) { // Use __construct instead of the class name for constructor in PHP5+\n        $this->score = $score; // Fixed typo here from scores to score\n        $this->name = $name;\n        $this->dept = $dept;\n        $this->date = $date;\n    }\n\n    function return_score(){\n        return array(\n            'score' => $this->score,\n            'name' => $this->name,\n            'dept' => $this->dept,\n            'date' => $this->date\n        );\n    }\n}\n\n$newscore = new Score("131313", "James", "Marketing", "19/05/2008");\nprint_r($newscore->return_score());