class Site_Mapper {\n    private $identityMap = [];\n\n    public function get($id) {\n        if (isset($this->identityMap[$id])) {\n            return $this->identityMap[$id];\n        }\n        // Simulate fetching data from a database\n        $data = ['id' => $id, 'name' => 'Site Name']; // This would be your real data fetching logic\n        $site = new Site($data);\n        $this->identityMap[$id] = $site;\n        return $site;\n    }\n}\n\nclass Site {\n    private $id;\n    private $name;\n\n    public function __construct($data) {\n        $this->id = $data['id'];\n        $this->name = $data['name'];\n    }\n\n    // getters and setters...\n}