$A = array(1, 2, 3, 4, 5, 6, 7, 8);\n$B = array(1, 2, 3, 4); // Example relationship set\n\ndo {\n    $C = array_intersect($A, $B);  // Calculate the intersection\n    if (!empty($C)) {\n        echo "Matching elements: " . implode(", ", $C) . "\n";\n        $A = array_diff($A, $B);     // Remove matching elements from A\n    }\n} while (!empty($C));               // Continue until no matches are found\n\necho "Remaining elements in A: " . (empty($A) ? 'None' : implode(", ", $A));