<?php\ntry {\n    // Create a new PDO instance for SQLite\n    $dbh = new PDO('sqlite:vets.db');\n    \n    // Set the PDO error mode to exception\n    $dbh->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    \n    // Prepare an SQL statement for execution\n    $stmt = $dbh->prepare("INSERT INTO vets(name,email,clinic,streetname,citystatezip,link,phone,fax,animal,region,visible) VALUES (:name,:email,:clinic,:streetname,:citystatezip,:link,:phone,:fax,:animal,:region,:visible)");\n    \n    // Bind parameters to statement variables\n    $stmt->bindParam(':name', $name);\n    $stmt->bindParam(':email', $email);\n    $stmt->bindParam(':clinic', $clinic);\n    $stmt->bindParam(':streetname', $streetname);\n    $stmt->bindParam(':citystatezip', $citystatezip);\n    $stmt->bindParam(':link', $link);\n    $stmt->bindParam(':phone', $phone);\n    $stmt->bindParam(':fax', $fax);\n    $stmt->bindParam(':animal', $animal);\n    $stmt->bindParam(':region', $region);\n    $stmt->bindParam(':visible', $visible);\n\n    // Execute the statement\n    $stmt->execute();\n    \n    echo "Data inserted successfully";\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n// Close the database connection\n$dbh = null;\n?>