for ($i = 0; $i < count($_FILES['uploadimg']['name']); $i++) {\n    $name = $_FILES['uploadimg']['name'][$i];\n    $type = $_FILES['uploadimg']['type'][$i];\n    $filepath = $_FILES['uploadimg']['tmp_name'][$i];\n    $size = getimagesize($filepath);\n    $error = $_FILES['uploadimg']['error'][$i];\n\n    switch ($error) {\n        case UPLOAD_ERR_OK:\n            // File uploaded successfully, proceed with processing\n            if ($size !== false && strpos($type, 'image') === 0) { // Ensure file is an image\n                $img = file_get_contents($filepath);\n                // Insert into database logic here\n                break;\n            } else {\n                echo "File $name is not a valid image.";\n            }\n            break;\n        case UPLOAD_ERR_INI_SIZE:\n        case UPLOAD_ERR_FORM_SIZE:\n            echo "File $name exceeds upload size limit.";\n            break;\n        default:\n            echo "An error occurred when uploading file $name: Error Code $error";\n    }\n}