class Custom_Decorator_SpanButtonLabel extends Zend_Form_Decorator_Abstract\n{\n    public function render($content)\n    {\n        $element = $this->getElement();\n        if (!$element instanceof Zend_Form_Element_Button) {\n            return parent::render($content);\n        }\n\n        $label = htmlspecialchars($element->getLabel(), ENT_QUOTES, 'UTF-8');\n        $buttonContent = "<span>{$label}</span>";\n\n        // Replace the default label with our custom content\n        $content = str_replace($label, $buttonContent, $content);\n\n        return parent::render($content);\n    }\n}