// Assuming $dom is your DOMDocument and $xpath is your DOMXPath\n$xpath = new DOMXPath($dom);\n$nodes = $xpath->query('//your/xpath/query');\n\n// Get the number of matched nodes\n$numNodes = $nodes->length;\necho "Number of nodes: " . $numNodes . "\n";\n\n// Iterate over each node\nfor ($i = 0; $i < $nodes->length; $i++) {\n    echo $dom->saveXML($nodes->item($i));\n}\n\n// Alternatively, using a foreach loop (since PHP 5.3)\nforeach ($nodes as $node) {\n    echo $dom->saveXML($node);\n}