<?php\n$url = "http://your-api-url/yourapi/getdata";\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n$response = curl_exec($ch);\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE); // Get the HTTP status code\nif ($httpCode == 200) {\n    echo $response;\n} else {\n    echo "Error: " . $httpCode;\n}\n\ncurl_close($ch);\n?>