$user_id = 12345; // Example user ID\n   $tmp_name = $_FILES['file']['tmp_name'];\n   $mime_type = mime_content_type($tmp_name);\n   $new_file_name = '';\n\n   switch ($mime_type) {\n       case 'image/jpeg':\n           $new_file_name = "$user_id.jpg";\n           break;\n       case 'image/png':\n           $new_file_name = "$user_id.png";\n           break;\n       case 'image/gif':\n           $new_file_name = "$user_id.gif";\n           break;\n       default:\n           echo "Invalid file format.";\n           exit;\n   }\n\n   // Move or save the file using $new_file_name\n   move_uploaded_file($tmp_name, "uploads/$new_file_name");