<?php\n$db_table = 'your_table_name'; // Replace with your actual table name\n$datetime = 'timestamp_column_name'; // Replace with your timestamp column\n\n// Database connection\n$conn = mysqli_connect('localhost', 'username', 'password', 'database_name');\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n$sql = "SELECT * FROM \n        (SELECT * FROM $db_table ORDER BY $datetime DESC LIMIT 100) AS last_100_entries\n        ORDER BY RAND() LIMIT 1";\n\n$result = mysqli_query($conn, $sql);\n\nif ($result && mysqli_num_rows($result) > 0) {\n    while ($row = mysqli_fetch_array($result)) {\n        echo "".$row['familyname']."";\n    }\n} else {\n    echo "No entries found or query execution failed: " . (mysqli_error($conn) ?? 'Unknown error');\n}\n\nmysqli_close($conn);\n?>