require_once('fpdf.php');\nrequire_once('fpdi.php');\n\n// initiate FPDI\n$pdf = new FPDI();\n\n// add a page (portrait mode by default)\n// set automatic page break to true with a margin of 10mm from bottom\n$pdf->SetAutoPageBreak(true, 10);\n\n// import the first page of the document as a template\n$tplIdx = $pdf->importPage(1);\n\n// get the size of the imported page\n$sizes = $pdf->getTemplateSize($tplIdx);\n$width = $sizes['w'];\n$height = $sizes['h'];\n\n// add another page with the correct orientation based on the template\n$pdf->AddPage($width > $height ? 'L' : 'P', [$width, $height]);\n\n// use the imported page as a template without any resizing or rotation\n$pdf->useTemplate($tplIdx);\n\n// output the resulting document\n$pdf->Output();