// Include NuSOAP library\nrequire_once('nusoap/lib/nusoap.php');\n\nfunction receiveFile($base64data) {\n    // Decode base64 data to binary\n    $binaryData = base64_decode($base64data);\n\n    // Specify the path for saving the received file\n    $filePath = '/path/to/save/received_file.ext';\n\n    // Write binary data to disk\n    if (file_put_contents($filePath, $binaryData)) {\n        return 'File saved successfully.';\n    } else {\n        return 'Failed to save the file.';\n    }\n}\n\n// Create a new instance of soap_server\n$server = new soap_server();\n\n// Configure SOAP Server\n$server->configureWSDL('Server', 'urn:Server');\n\n// Register WSDL function\n$server->register(\n    'receiveFile',\n    array('base64data' => 'xsd:string'),  // input parameter\n    array('return' => 'xsd:string')        // return type\n);\n\n// Handle request from client\n$HTTP_RAW_POST_DATA = isset($HTTP_RAW_POST_DATA) ? $HTTP_RAW_POST_DATA : '';\n$server->service($HTTP_RAW_POST_DATA);