<?php\nif (isset($_GET["page"]) && is_numeric($_GET['page'])) {\n    // Use PDO to prevent SQL injection\n    try {\n        $con = new PDO("mysql:host=localhost;dbname=jmurano_pages", "username", "password");\n        $con->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        \n        // Prepare the statement with a placeholder for the page ID\n        $stmt = $con->prepare("SELECT title, content FROM pageContent WHERE pageID=:pageID LIMIT 1");\n        $stmt->bindParam(':pageID', $_GET['page'], PDO::PARAM_INT);\n\n        // Execute the query\n        $stmt->execute();\n        \n        // Fetch the result\n        $result = $stmt->fetch(PDO::FETCH_ASSOC);\n        \n        if ($result) {\n            echo "<title>" . htmlspecialchars($result['title']) . "</title>\n";\n            echo "</head>\n<body>\n";\n            echo nl2br(htmlspecialchars($result['content']));\n            echo "\n</body>\n</html>";\n        } else {\n            echo "Page not found.";\n        }\n        \n    } catch(PDOException $e) {\n        die("Could not connect: " . $e->getMessage());\n    }\n} else {\n    echo "Invalid page parameter.";\n}\n?>