// Client script\n\nfunction sendFileToServer($filePath, $url) {\n    // Read binary file data\n    $binaryData = file_get_contents($filePath);\n\n    // Encode data with Base64 encoding\n    $base64data = base64_encode($binaryData);\n\n    // Create NuSOAP client\n    $client = new nusoap_client($url, 'wsdl');\n\n    // Check for errors in the client\n    $err = $client->getError();\n    if ($err) {\n        echo 'Constructor error: ' . $err;\n        die();\n    }\n\n    // Call remote procedure with Base64 data\n    $result = $client->call('receiveFile', array('base64data' => $base64data));\n\n    // Check for errors in the call\n    if ($client->fault) {\n        echo 'Fault: ';\n        print_r($result);\n    } else {\n        $err = $client->getError();\n        if ($err) {\n            echo 'Error: ' . $err;\n        } else {\n            echo 'Result: ' . $result;\n        }\n    }\n}\n\n// Example usage\nsendFileToServer('path/to/local/file.ext', 'http://yourwebserviceURL/server.php');